import React, { useEffect, useState } from 'react';
import { Layout, Menu } from 'antd';
import {
  UilChartPieAlt,
  UilShoppingCart,
  UilPricetagAlt,
  UilUsersAlt,
  UilUsdSquare,
  UilCommentAltHeart,
  UilUserSquare,
  UilSignOutAlt
} from "@iconscout/react-unicons"
import './layout.css';
import { Link, useNavigate} from 'react-router-dom';
import { useSelector } from 'react-redux';

const { Header, Sider, Content } = Layout;

const LayoutApp = ({children}) => {
  const {cartItems, loading} = useSelector(state => state.rootReducer);

  const [collapsed, setCollapsed] = useState(false);
  const navigate = useNavigate();


  const toggle = () => {
    setCollapsed(!collapsed);
  };

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems))
  }, [cartItems]);

    return (
        <Layout>
          {loading}
          <Sider trigger={null} collapsible collapsed={collapsed}>
            <div className="logo">
                <a href="#">
                    <img class="image" src="logo192.png" alt="logo"/>
                </a>
            </div>
            <Menu theme="dark" mode="inline" defaultSelectedKeys={window.location.pathname}>
                <Menu.Item key='/' icon={<HomeOutlined />}>
                    <Link to="/">Home/Order</Link>
                </Menu.Item>
                <Menu.Item key='/bills' icon={<MoneyCollectOutlined />}>
                    <Link to="/bills">Bills/Dashboard</Link>
                </Menu.Item>
                <Menu.Item key="/products" icon={<InboxOutlined />}>
                    <Link to="/products">Products</Link>
                </Menu.Item>
                <Menu.Item key='/customers' icon={<UserSwitchOutlined />}>
                    <Link to="/customers">Customers/Member</Link>
                </Menu.Item>
                <Menu.Item key='/logout' icon={<LogoutOutlined />} onClick={() => {localStorage.removeItem("auth"); navigate("/login");}}>
                    <Link to="/logout">Log Out</Link>
                </Menu.Item>
            </Menu>
          </Sider>
          <Layout className="site-layout">
            <Header className="site-layout-background" style={{ padding: 0 }}>
              {React.createElement(collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                className: 'trigger',
                onClick: () => toggle,
              })}
            <div className="cart-items" onClick={()=> navigate('/cart')}>
                <ShoppingCartOutlined />
                <span className="cart-badge">{cartItems.length}</span>
            </div>
            </Header>
            <Content
              className="site-layout-background"
              style={{
                margin: '24px 16px',
                padding: 24,
                minHeight: 280,
              }}
            >
              {children}
            </Content>
          </Layout>
        </Layout>
      );
    };
    
export default LayoutApp;
